import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate, useParams } from "react-router-dom";

export default function Update() {
    const navigate = useNavigate();
    const { id } = useParams();

    const [employee, setEmployee] = useState('');
    const [department, setDepartment] = useState('');

    useEffect(() => {
        fetchEmployee();
    }, []);

    const fetchEmployee = async () => {
        try {
            const { data } = await axios.get(`http://127.0.0.1:8000/update/${id}`);
            const { employee, department } = data.mission;
            setEmployee(employee);
            setDepartment(department);
        } catch (error) {
            console.log(error.response?.data.message);
        }
    };

    const updateEmployee = async (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append('_method', 'PATCH');
        formData.append('employee', employee);
        formData.append('department', department);

        try {
            const { data } = await axios.post(`http://127.0.0.1:8000/update/${id}`, formData);
            console.log(data.message);
            navigate('/');
        } catch (error) {
            if (error.response?.status === 422) {
                console.log(error.response.data.errors);
            } else {
                console.log(error.response?.data.message);
            }
        }
    };

    return (
        <center>
            <div className="center-container">
                <form onSubmit={updateEmployee} className="custom-form">
                    <table>
                        <tbody>
                            <tr>
                                <td><b>Employee</b></td>
                                <td>
                                    <input 
                                        type="text" 
                                        name="employee" 
                                        value={employee} 
                                        onChange={(e) => setEmployee(e.target.value)} 
                                        className="my-4" 
                                    />
                                </td>
                            </tr>
                            <tr>
                                <td><b>Department</b></td>
                                <td>
                                    <input 
                                        type="text" 
                                        name="department" 
                                        value={department} 
                                        onChange={(e) => setDepartment(e.target.value)} 
                                        className="my-4"
                                    />
                                </td>
                            </tr>
                            <tr>
                                <td></td>
                                <td>
                                    <button type="submit" className="form-control btn btn-secondary">Update</button>
                                </td>
                            </tr>
                        </tbody>
                    </table>                           
                </form>
            </div>
        </center>
    );
}
