import React, { useState ,useEffect} from "react";
import axios from "axios";
import { useNavigate ,useParams} from "react-router-dom";

export default function Update() {

    const navigate = useNavigate();
    const { id } = useParams();
    
    const [name,setName] = useState('')
    const [birthday,setBirthday] = useState('')

    useEffect(()=>{
        fetchPlayer();
    },[])

    const fetchPlayer = async() =>{
        await axios.get(`http://127.0.0.1:8000/api/player/${id}`)
        .then(({ data }) => {
            const { name, birthday } = data.player
            setName(name)
            setBirthday(birthday)

        }).catch(({ response: {data} }) => {
            console.log(data.message)
        })
    }

    const updatePlayer = async (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append('_method', 'PATCH');
        formData.append('name', name);
        formData.append('birthday', birthday);
    
        await axios.post(`http://127.0.0.1:8000/api/player/${id}`, formData)
            .then(({ data }) => {
                console.log(data.message);
                navigate('/all_players');
            })
            .catch(({ response }) => {
                if (response.status === 422) {
                    console.log(response.data.errors);
                } else {
                    console.log(response.data.message);
                }
            });
    }      

    return (
        <center>
            <div className="container my-3">
                <form onSubmit={updatePlayer}>
                    <table>
                        <tr>
                            <td>
                                <b>Name</b>
                            </td>
                            <td>
                                <input type="text" name="name" value={name} onChange={(e)=>{setName(e.target.value)}} className="my-4"/>                                </td>
                            </tr>
                            <tr>
                                <td>
                                    <b>Birthday</b>
                                </td>
                                <td>
                                    <b> : </b>
                                    <input type="date" name="birthday" value={birthday} onChange={(e)=>{setBirthday(e.target.value)}} className="my-4"/>
                                </td>
                            </tr>

                            <tr>
                                <td></td>
                                <td>
                                    <button className="form-control btn btn-secondary btn-block"><LuEdit/> Update</button>                       
                                </td>
                            </tr>
                        </table>                           
                    </form>
            </div>
        </center>
    )
}
