import React, { useState ,useEffect} from "react";
import axios from "axios";
import { useNavigate ,useParams} from "react-router-dom";

export default function Update() {

    const navigate = useNavigate();
    const { id } = useParams();
    
    const [employee,setEmployee] = useState('')
    const [department,setDepartment] = useState('')

    useEffect(()=>{
        fetchEmployee();
    },[])

    const fetchEmployee = async() =>{
        await axios.get(`http://127.0.0.1:8000/api/${id}/update`)
        .then(({ data }) => {
            const { employee, department } = data.player
            setEmployee(employee)
            setDepartment(department)

        }).catch(({ response: {data} }) => {
            console.log(data.message)
        })
    }

    const updatePlayer = async (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append('_method', 'PATCH');
        formData.append('employee', employee);
        formData.append('department', department);
    
        await axios.post(`http://127.0.0.1:8000/api/${id}/update`, formData)
            .then(({ data }) => {
                console.log(data.message);
                navigate('/');
            })
            .catch(({ response }) => {
                if (response.status === 422) {
                    console.log(response.data.errors);
                } else {
                    console.log(response.data.message);
                }
            });
    }      

    return (
        <center>
            <div className="container my-3">
                <form onSubmit={updatePlayer}>
                    <table>
                        <tr>
                            <td>
                                <b>Name</b>
                            </td>
                            <td>
                                : <input type="text" name="employee" value={employee} onChange={(e)=>{setEmployee(e.target.value)}} className="my-4"/>
                            </td>
                        </tr>
                        <tr>
                            <td>
                                <b>department</b>
                            </td>
                            <td>
                                : <input type="text" name="department" value={department} onChange={(e)=>{setDepartment(e.target.value)}} className="my-4"/>
                            </td>
                        </tr>
                        <tr>
                            <td></td>
                            <td>
                                <button className="form-control btn btn-secondary btn-block"> Update</button>                       
                            </td>
                        </tr>
                    </table>                           
                </form>
            </div>
        </center>
    )
}
