import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

export default function Liste() {
  const [missions, setMissions] = useState([]);

  useEffect(() => {
    fetchMissions();
  }, []);

  const fetchMissions = async () => {
    try {
      const response = await axios.get('http://127.0.0.1:8000');
      setMissions(response.data);
    } catch (error) {
      console.error('Error fetching missions:', error);
      // Handle error if needed
    }
  };

  const deleteMission = async (id) => {
    console.log("Deleting mission with id:", id); // Log the id before the delete request

    const confirmDelete = window.confirm('Are you sure you want to delete this mission?');

    if (confirmDelete) {
      try {
        await axios.delete(`http://127.0.0.1:8000/${id}/`);
        fetchMissions();
      } catch (error) {
        console.error('Error deleting mission:', error);
        // Handle error if needed
      }
    }
  };

  return (
    <div className="container">
      <Link to='/create'>
        <button className="btn btn-warning my-3">Create Mission</button>
      </Link>
      <table width="100%" className="text-center">
        <thead>
          <tr>
            <th>Description</th>
            <th>Deadline</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
  {missions.map((mission) => (
    <tr key={`mission_${mission.id}`}>
      <td>{mission.employee}</td>
      <td>{mission.department}</td>
      <td>               
        <button onClick={() => deleteMission(mission.id)} className="btn btn-danger mx-1">Delete</button>
      </td>       
    </tr>
  ))}
</tbody>
      </table>  
    </div> 
  )
}
