import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate, useParams } from "react-router-dom";

export default function Update() {
  const navigate = useNavigate();
  const { id } = useParams();
  const [employee, setEmployee] = useState("");
  const [department, setDepartment] = useState("");

  useEffect(() => {
    const fetchEmployee = async () => {
      try {
        const response = await axios.get(`http://127.0.0.1:8000/update/${id}`);
        const { employee, department } = response.data;
        setEmployee(employee);
        setDepartment(department);
      } catch (error) {
        console.error("Error fetching employee:", error);
      }
    };

    fetchEmployee();
  }, [id]); // Dependency array ensures fetch only on initial render and ID change

  const updateEmployee = async (e) => {
    e.preventDefault();
    const formData = new FormData(); // Use FormData for potentially handling file uploads
    formData.append("employee", employee);
    formData.append("department", department);

    await axios.put(`http://127.0.0.1:8000/update/${id}/`, formData, {
      headers: { "Content-Type": "multipart/form-data" },
      params: { _method: "PATCH" }, // Simulate PUT for Django backend that might not support PATCH
    })
      .then(({ data }) => {
        console.log(data.message);
        navigate("/"); // Redirect to a suitable location after successful update
      })
      .catch(({ response }) => {
        if (response.status === 422) {
          console.log(response.data.errors); // Handle specific validation errors if needed
          // Display appropriate error messages to the user
        } else {
          console.log(response.data.message); // Handle general errors
        }
      });
  };

    return (
        <center>
            <div className="container my-3">
                <form onSubmit={updateEmployee}>
                    <table>
                        <tr>
                            <td><b>Employee</b></td>
                            <td>: <input type="text" name="employee" value={employee} onChange={(e) => setEmployee(e.target.value)} className="my-4" /></td>
                        </tr>
                        <tr>
                            <td><b>Department</b></td>
                            <td>: <input type="text" name="department" value={department} onChange={(e) => setDepartment(e.target.value)} className="my-4" /></td>
                        </tr>
                        <tr>
                            <td></td>
                            <td><button className="form-control btn btn-secondary btn-block">Update</button></td>
                        </tr>
                    </table>
                </form>
            </div>
        </center>
    )
}
