import React, { useState ,useEffect} from "react";
import axios from "axios";
import { useNavigate ,useParams} from "react-router-dom";

export default function Update() {
    const navigate = useNavigate();
    const { id } = useParams();

    const [employee,setEmployee] = useState('')
    const [department, setDepartment] = useState('')

    useEffect(()=>{
        fetchEmployee();
    },[])

    const fetchEmployee = async() =>{
        await axios.get(`http://127.0.0.1:8000/api/mission/${id}`)
        .then(({ data }) => {
            const { employee, department } = data.mission
            setEmployee(employee)
            setDepartment(department)
        }).catch(({ response: {data} }) => {
            console.log(data.message)
        })
    }

    const updateEmployee = async (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append('_method', 'PATCH')
        formData.append('employee', employee)
        formData.append('department', department)
        await axios.post('http://127.0.0.1:8000/api/mission/' + id, formData)
        .then(({ data }) => {
            console.log(data.message)
            navigate('/')
        }).catch(({ response }) => {
            if (response.status == 422) {
                console.log(response.data.errors)
            } else {
                console.log(response.data.message)
            }
        })
    }

    return (
        <center>
            <div class="center-container">
                <form onSubmit={updateEmployee} class="custom-form">
                    <table>
                        <tr>
                            <td>
                                <b>employee</b>
                            </td>
                            <td>
                                <textarea name="employee" value={employee} cols="60" rows="3" className="my-4" onChange={(e)=>{setEmployee(e.target.value)}}></textarea>
                            </td>
                        </tr>
                        <tr>
                            <td>
                                <b>department</b>
                            </td>
                            <td>
                                <input type="date" name="department" value={department} onChange={(e)=>{setDepartment(e.target.value)}} className="form-control my-4"/>
                            </td>
                        </tr>
                        <tr>
                            <td></td>
                            <td>
                                <button className="form-control btn btn-secondary"><LuEdit/> Update</button>
                            </td>
                        </tr>
                    </table>                           
                </form>
            </div>
        </center>
    )
}
