import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

export default function Liste() {
  const [mission, setMission] = useState([]);

  useEffect(() => {
    fetchMission();
  }, []);

  const fetchMission = async () => {
    try {
      const response = await axios.get('http://127.0.0.1:8000');
      setMission(response.data);
    } catch (error) {
      console.error('Error fetching missions:', error);
      // Handle error if needed
    }
  };

  const deleteMission = async (id) => {
    const confirmDelete = window.confirm('Are you sure you want to delete this mission?');

    if (confirmDelete) {
      try {
        await axios.delete(`http://127.0.0.1:8000/${id}`);
        fetchMission();
      } catch (error) {
        console.error('Error deleting mission:', error);
        // Handle error if needed
      }
    }
  };

  return (
    <div className="container">
      <Link to='/create'>
        <button className="btn btn-warning my-3">Create Mission</button>
      </Link>
      <table width="100%" className="text-center">
        <thead>
          <tr>
            <th>Description</th>
            <th>Deadline</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {mission.map((item) => (
            <tr key={item.id}>
              <td>{item.employee}</td>
              <td>{item.department}</td>
              <td>               
                <button onClick={() => deleteMission(item.id)} className="btn btn-danger mx-1">Delete</button>
              </td>       
            </tr>
          ))}
        </tbody>
      </table>  
    </div> 
  )
}
